mapper.select:
select g.GRP_ID grpId, g.GRP_NAME grpName, g.CREATE_TIME createTime, g.REMARK remark, (select count(gm1.UDN) from TB_GROUP_MS gm1 where gm1.GRP_ID=g.GRP_ID) count from TB_GROUP_INFO g inner join TB_GROUP_MS gm on gm.GRP_ID = g.GRP_ID where 1=1
and g.IS_TEMP = #{isTemp}
and gm.UDN = #{udn}
limit ${(pageNow-1)*pageSize}, #{pageSize}

        select count(1)
        from TB_GROUP_INFO g
        inner join TB_GROUP_MS gm on gm.GRP_ID = g.GRP_ID
        where 1=1
        
            and g.IS_TEMP = #{isTemp}
        
        
            and gm.UDN = #{udn}

select g.GRP_ID,
       g.GRP_NAME
from TB_GROUP_INFO g where g.PGRP_ID = #{pgrpId}
