mapper.update:
update TB_GROUP_MS

    
        is_sub_call = #{isSubCall,jdbcType=VARCHAR},
    

where grp_id = #{grpId}
and udn = #{udn}

mapper.select:
select gm.GRP_ID,grp.GRP_NAME,gm.UDN,gm.IS_SUB_CALL from TB_GROUP_MS gm
inner join TB_GROUP_INFO grp on grp.GRP_ID=gm.GRP_ID
where gm.UDN = #{udn}

select gm.GRP_ID,gm.UDN,u.USER_NAME,gm.IS_SUB_CALL from TB_GROUP_MS gm
 inner join TB_USER u on gm.UDN=u.UDN
 where gm.GRP_ID = #{grpId}
 and gm.UDN != #{udn}
