mapper.select:
        select l.USER_NAME,
               l.CONTENT,
               l.OPERATION_TIME,
               l.OTHER
        from TB_LOG l where 1=1
        
            and l.USER_NAME = #{userName}
        
        
            and l.OPERATION_TIME BETWEEN #{startTime} AND #{endTime}
        

        limit ${(pageNow-1)*pageSize}, #{pageSize}

        select count(1)
        from TB_LOG l where 1=1
        
            and l.USER_NAME = #{userName}
        
        
            and l.OPERATION_TIME BETWEEN #{startTime} AND #{endTime}
