mapper.select:
select distinct m.UDN, m.ONLINE_STATUS, m.`STATUS`, m.IMSI, m.TYPE, m.IS_TCN, m.IS_SUB_GROUP, m.IS_SUB_REGISTER, gm1.GRP_ID, u.USER_NAME from TB_MS_INFO m inner join TB_GROUP_MS gm1 on gm1.UDN=m.UDN inner join TB_USER u on u.UDN=m.UDN where gm1.UDN != #{udn}
and gm1.GRP_ID in ( select gm2.GRP_ID from TB_GROUP_MS gm2 where gm2.UDN = #{udn})
and gm1.GRP_ID = #{grpId}
limit ${(pageNow-1)*pageSize}, #{pageSize}

select count(1) from TB_MS_INFO m inner join TB_GROUP_MS gm1 on gm1.UDN=m.UDN where gm1.UDN != #{udn}
and gm1.GRP_ID in ( select gm2.GRP_ID from TB_GROUP_MS gm2 where gm2.UDN = #{udn})
and gm1.GRP_ID = #{grpId}
